} ) {

const classes = className('px-3 py-1.5 border', {
    'border-blue-500 bg-blue-500 text-white' : primary

})


    return <button className="px-3 py-1.5 border border-blue-600 text-white bg-blue-500">{children}</button>
}


Button.propTypes = {
    checkVariationValue: ({    primary,
        secondary,
        success,
        warning,
        danger}) => {

            const count = Number(!!primary)
            +Number(!!secondary)
            +Number(!!success)
            +Number(!!warning)
            Number(!!danger)

            if(count >1) {
                return new Error('Only one of primary secondary success warning or danger allowed ')
            }
    }   
};

export default Button;
